(1094, 'addcode_settings', 'a:4:{s:4:\"css1\";s:4979:\".banggia-title {\r\n    font-size: 24px;\r\n    color: var(--texta);\r\n    text-align: center;\r\n    padding: 7px;\r\n    margin: auto;\r\n    margin-bottom: 10px;\r\n    position: relative;\r\n    border-radius: 10px;\r\n}\r\n.box-gui{\r\n	display:grid;\r\n	grid-template-columns: 1.4fr 1fr;\r\n	grid-column-gap:20px;\r\n	margin-bottom:20px;\r\n	padding:20px;\r\n	background-image: url(https://taxiquynhonpro.com/wp-content/uploads/2023/01/taxiquynhonnew.png);\r\n	background-size:cover;\r\n	border-radius:10px;\r\n	font-size:15px!important;\r\n}\r\n@media (max-width: 800px) {\r\n	.box-gui{grid-template-columns: 1fr;}\r\n}\r\n.gui-grid{\r\n	display:grid;\r\n	grid-template-columns: 1fr 1fr;\r\n	grid-column-gap:10px;\r\n}\r\n.gui-grid-p p{\r\n	display:grid;\r\n	grid-template-columns: 1fr 1fr;\r\n	grid-column-gap:10px;\r\n}\r\n.input-gui-icon {\r\n    position: absolute;\r\n    left: 10px;\r\n    top: calc(37% - 0.5em);\r\n    color: var(--textnote);\r\n}\r\n@media (max-width: 500px) {\r\n	.gui-grid-new{grid-template-columns: 1fr;}\r\n}\r\n.gui-grid-new input{\r\n	height:35px;\r\n}\r\n#gui-yeucau{\r\n	max-height:100px;\r\n}\r\n.dat-title {\r\n    padding: 10px;\r\n    background: #ffffffc2;\r\n    color: #606060;\r\n    font-weight: bold;\r\n    border-radius: 10px 10px 0px 0px;\r\n    text-align: center;\r\n}\r\n.form-dat .box-form{\r\n	background:#ffffff87;\r\n	padding:10px;\r\n	border-radius: 0px 0px 10px 10px;\r\n	animation: popup 1s;\r\n}\r\n.form-dat .box-form p, .form-dat form p{margin:0px;}\r\n.form-dat input{\r\n	width:100% !important;\r\n	padding:7px;\r\n	border-radius:10px;\r\n	margin-bottom:10px;\r\n	background:#ffffffbf;\r\n	color:#333;\r\n	padding-left:35px;\r\n	font-size:15px!important;\r\n}\r\n.form-dat textarea, .form-dat select{\r\n	width:100% !important;\r\n	padding:7px;\r\n	border-radius:10px;\r\n	margin-bottom:10px;\r\n	background:#ffffffbf;\r\n	font-size:15px!important;\r\n	color:#333;\r\n}\r\n.form-dat #gui-dat{\r\n	background:var(--texta);\r\n	color:#fff;\r\n	margin-bottom:0px;\r\n	width:150px !important;\r\n	border-radius:10px;\r\n	padding:7px;\r\n	border-radius:10px;\r\n	padding-left:0px;\r\n	font-size:15px!important;\r\n}\r\n.gui-date-dive {\r\n    margin-bottom: 10px;\r\n    background: #ffdf0059;\r\n    padding: 7px;\r\n    border-radius: 10px;\r\n}\r\nspan.wpcf7-spinner {\r\n}\r\n.wpcf7-response-output {\r\n    margin: 0px !important;\r\n    margin-top: 10px !important;\r\n    background: #ffffff7a;\r\n    border-radius: 10px;\r\n	border:none !important;\r\n}\r\n.title-gui {\r\n    display: grid;\r\n    align-items: center;\r\n    font-size: 67px;\r\n    text-align: center;\r\n    text-transform: uppercase;\r\n    font-weight: 900;\r\n    color: #fff;\r\n    text-shadow: 0px 0px 3px #000;\r\n	animation: phone-outer 1000ms infinite;\r\n}\r\n.phucvu-box {\r\n    display: grid;\r\n    grid-template-columns: 1fr 1fr 1fr 1fr;\r\n    grid-column-gap: 20px;\r\n    grid-row-gap: 20px;\r\n    text-align: center;\r\n    margin-bottom: 20px;\r\n    padding: 20px;\r\n    background: #efa5002e;\r\n    border-radius: 10px;\r\n    border: 2px solid var(--texta);\r\n}\r\n@media (max-width: 900px) {\r\n	.phucvu-box{grid-template-columns: 1fr 1fr;}\r\n}\r\n.phucvu-box img {\r\n    width: 100%;\r\n    height: 250px;\r\n    border-radius: 10px 80px 10px 80px;\r\n    object-fit: cover;\r\n	transition: all .3s ease;\r\n}\r\n.phucvu-box img:hover{transform: scale(0.9);}\r\n.phucvu-box span {\r\n    display: block;\r\n    font-size: 16px;\r\n    font-weight: 900;\r\n	margin-top: 10px;\r\n}\r\n.phucvu-title {\r\n    text-align: center;\r\n    font-size: 35px;\r\n    margin-bottom: 10px;\r\n    text-transform: uppercase;\r\n    font-weight: 900;\r\n    color: var(--texta);\r\n}\r\nspan.wpcf7-not-valid-tip {\r\n    font-size: 12px !important;\r\n    margin-bottom: 7px;\r\n    margin-top: -15px;\r\n    position: absolute;\r\n    z-index: 1;\r\n    background: #ff444494;\r\n    color: #fff;\r\n    padding: 0px 7px;\r\n    border-radius: 5px;\r\n}\r\n.title-vechungtoi {\r\n    text-align: center;\r\n	margin-top: 30px;\r\n}\r\n.title-vechungtoi span {\r\n    text-align: center;\r\n    font-size: 40px;\r\n    margin-bottom: 10px;\r\n    text-transform: uppercase;\r\n    font-weight: 900;\r\n    color: var(--texta);\r\n    display: block;\r\n    border-radius: 40px;\r\n}\r\n.vechungtoi {\r\n    display: grid;\r\n    grid-template-columns: 1fr 1fr 1fr 1fr;\r\n    grid-column-gap: 20px;\r\n    grid-row-gap: 20px;\r\n    text-align: center;\r\n    margin-bottom: 30px;\r\n    margin-top: 30px;\r\n    border-radius: 40px;\r\n}\r\n@media (max-width: 900px) {\r\n	.vechungtoi{grid-template-columns: 1fr 1fr;}\r\n}\r\n.vechungtoi div span{\r\n	text-align: center;\r\n    font-size: 16px;\r\n    margin-bottom: 10px;\r\n    text-transform: uppercase;\r\n    font-weight: 900;\r\n    color: var(--texta);\r\n	display:block;\r\n}\r\n.vechungtoi i {\r\n    font-size: 35px;\r\n    display: block;\r\n    width: 80px;\r\n    height: 80px;\r\n    margin: auto;\r\n    background: var(--texta);\r\n    line-height: 80px;\r\n    margin-bottom: 10px;\r\n    border-radius: 100%;\r\n    color: #fbffb6;\r\n    border: 5px solid var(--comment);\r\n}\r\n.vechungtoi div {\r\n    background: #ccc6;\r\n    padding: 15px;\r\n    border-radius: 10px;\r\n	border:3px solid #ccc6;\r\n}\r\n.vechungtoi div:hover{\r\n	border:3px solid var(--texta);\r\n	background:#f1d3001f;\r\n}\r\n\";s:7:\"script1\";s:0:\"\";s:7:\"script2\";s:0:\"\";s:7:\"script3\";s:0:\"\";}', 'yes'),
