function fox_onchat_add_options_link() {
	add_submenu_page ('fox-options', 'On chat', '<i class="fa-regular fa-message"></i> On chat', 'manage_options', 'onchat-options', 'fox_onchat_options_page');
}
add_action('admin_menu', 'fox_onchat_add_options_link');
function fox_onchat_register_settings() {
	register_setting('onchat_settings_group', 'onchat_settings');
}
add_action('admin_init', 'fox_onchat_register_settings');

