<div class="simple-audio-player" id="simp" data-config='{"shide_top":false,"shide_btm":false,"auto_load":false}'>
  <div class="simp-playlist">
    <ul style="counter-reset: Serial;">
        <?php
        if (empty(get_post_meta( $post->ID, 'story_audio3', true ))){ 
        
		$story_audio2 = get_post_meta($post->ID, 'story_audio2', true);
	    $story_audio2 = explode(',', $story_audio2);
	    $story_audio21 = get_post_meta($post->ID, 'story_audio21', true);
	    $story_audio21 = explode(',', $story_audio21);
	    $audio_link_all1 = array_combine($story_audio2, $story_audio21);
		
        if(isset($audio_link_all1) && is_array($audio_link_all1)) {
			$o = 0;
            foreach($audio_link_all1 as $value => $id){
			$o++; ?>
            <li><span class="simp-source" data-src="<?php echo $value; ?>"><?php if(!empty($id)){ echo '<i class="fa-regular fa-circle-play" style="margin-right:7px"></i> '. $id; } else {echo '<i class="fa-regular fa-circle-play" style="margin-right:7px"></i> '. __('Chương', 'fox') . ' '. $o;} ?></span><span class="simp-desc"><?php echo get_post_meta( $post->ID, 'story_audio1', true ); ?></span></li>
			<?php
            }
        }
        
        } else {
            $listau = get_post_meta( $post->ID, 'story_audio3', true );
            $fruits = explode("\n", str_replace("\r", "",  $listau));
            $o = 0;
            foreach ($fruits as $value) {
            $o++;
            echo '<li><span class="simp-source" data-src="'.$value.'"><i class="fa-regular fa-circle-play" style="margin-right:7px"></i> '. __('Chương', 'fox') .' '.$o.'</span><span class="simp-desc">'.get_post_meta( $post->ID, 'story_audio1', true ).'</span></li>';
            }
        }
        ?>
    </ul>
  </div>
</div>
